import "./css/style.css"

function Header(){
  return(
    <header>
        <h1>簡易イベント表示</h1>
    </header>
  );
}

function Event(props){
  return(
    <div className="event_block">
      <div className="date">
        <span>11</span>
        <span>/</span>
        <span>4</span>
      </div>{/* date */}
      <div className="time">
        <dl>
          <dt>open</dt>
          <dd>16:30</dd>
        </dl>
        <dl>
          <dt>start</dt>
          <dd>18:30</dd>
        </dl>
      </div>{/* date */}
      <div className="title">
        <h2><span>{props.title}</span></h2>
      </div>{/* title */}
      <div className="place">
        <p><span>{props.place}</span></p>
      </div>{/* place */}
    </div>
  );
}

function Main(){
  const events = [
    {title: "鼓童ワン・アース・ツアー 童", place: "新潟県民会館"},
    {title: "鼓童ワン・アース・ツアー 巡", place: "神奈川県民会館"},
    {title: "鼓童ワン・アース・ツアー 鼓", place: "石川県民会館"},
    {title: "鼓童ワン・アース・ツアー 打男", place: "埼玉県民会館"},
    {title: "鼓童ワン・アース・ツアー 鼓", place: "福島県民会館"},
  ];
  const filteredEvents = events.filter(
    (title) => {
      return title == "鼓童ワン・アース・ツアー 鼓";
    }
  );
  return(
    <main>
      <section className="event_section">
        <div className="main_width">
          <div className="event_group">

            {events.map((event) => {
              return (
                <Event title={event.title} place={event.place}/>
              );
            })}
          </div>{/* event_group */}
        </div>{/* main_width */}
      </section>
    </main>
  );
}



function App() {
  return (
    <div>
      <Header />
      <Main />

    </div>
  );
}

export default App;
