import { useEffect, useState } from "react";
import "./css/style.css"


function Form(props) {
  const {events, changeFilter}= props;
  function handleSubmit(event) {//このへん犬ギャラリーからコピー
    //event.preventDefault();
    const { selectedTitle } = event.target.elements;
    changeFilter(selectedTitle.value);
  }
  function change(event){
    event.preventDefault();
    console.log('かわったよ')
  };
  return (
    <form onSubmit={change}>
      <div className="select_box">
        <select name="selectedTitle" defaultValue={events[0].title}>
          {events.map((event) => {
            return (
              <option  key={event.id} value={event.title}>{event.title}</option>
            );
          })}
        </select>
      </div>
      <div className="select_btn">
        <button type="submit" className="button is-dark">
           Reload
        </button>
      </div>
    </form>
  );
}


function Header(props){
  const {events, changeFilter} = props;
  return(
    <header>
        <h1>簡易イベント表示</h1>
        <Form events={events} changeFilter={changeFilter}/>
    </header>
  );
}

function Event(props){
  return(
    <div key={props.id} className="event_block">
      <div className="num">
        <span>No.</span>
        <span>{props.id}</span>
      </div>
      <div className="date">
        <span>11</span>
        <span>/</span>
        <span>4</span>
      </div>{/* date */}
      <div className="time">
        <dl>
          <dt>open</dt>
          <dd>16:30</dd>
        </dl>
        <dl>
          <dt>start</dt>
          <dd>18:30</dd>
        </dl>
      </div>{/* date */}
      <div className="title">
        <h2><span>{props.title}</span></h2>
      </div>{/* title */}
      <div className="place">
        <p><span>{props.place}</span></p>
      </div>{/* place */}
    </div>
  );
}

function Main(props){
  const {events} = props;
  return(
    <main>
      <section className="event_section">
        <div className="main_width">
          <div className="event_group">

            {events.map((event) => {
              return (
                <Event id={event.id} title={event.title} place={event.place}/>
              );
            })}
          </div>{/* event_group */}
        </div>{/* main_width */}
      </section>
    </main>
  );
}

function App() {
  const events = [
    {id: 1, title: "鼓童ワン・アース・ツアー 童", place: "新潟県民会館"},
    {id: 2, title: "鼓童ワン・アース・ツアー 巡", place: "神奈川県民会館"},
    {id: 3, title: "鼓童ワン・アース・ツアー 鼓", place: "石川県民会館"},
    {id: 4, title: "鼓童ワン・アース・ツアー 打男", place: "埼玉県民会館"},
    {id: 5, title: "鼓童ワン・アース・ツアー 鼓", place: "福島県民会館"},
  ];

  ////このあたりでやりたいこと・ヘッダーで選んだタイトルでのソート////
  //const [filterTitle, setfilterTitle] = useState(events[2].title);
  //setfilterTitle(events[4].title);
  //let filterTitle = events[2].title;//フィルタリング用タイトルの設定
  //filterTitle = events[0].title;//フィルタリング用タイトルの更新

  /* function changeFilterTitle(slectedTitle){//フィルタリング用タイトルを更新するための関数
    filterTitle = slectedTitle;
  } */

  //changeFilterTitle("鼓童ワン・アース・ツアー 童");//関数の使用
  //console.log(filterTitle);//関数の確認

  /* const filteredEvents = events.filter(//実際のフィルタリング
    (event) => {
      return event.title == filterTitle;
    }
  ); */

  //const [filteredEvents, setFilteredEvents] = useState(events);

  /* ここの関数が上手くいっていない
  フィルタータイトルをセレクトされたタイトルに更新した後に、実際のフィルタリングを行いたい */
  let filteredEvents = events;
  function changeFilter(filterTitle){//フィルタリング用タイトルを更新するための関数
    filteredEvents = events.filter(//実際のフィルタリング
      (event) => {
        return event.title == filterTitle;
    });
  }

  changeFilter("鼓童ワン・アース・ツアー 童");


  return (
    <div>
      <Header events = {events} changeFilter={changeFilter}/>
      <Main events = {filteredEvents} />
    </div>
  );
}

export default App;
